import React, { Fragment, useState } from 'react';

const Formulario = () => {

    //Crear State de citas
    const [cita, setCita] = useState({
        mascota:'',
        propietario:'',
        fecha:'',
        hora:'',
        sintomas:''
    });

    //Funcion que se ejecuta cada que el usuario escribe en un input

    const handleChange = (e) =>{
        console.log(e.target.name)
        setCita({
            ...cita,  //Este wey es el de la magia !!!!
            [e.target.name]:e.target.value 
        })
    }
    //Extraer los valores de cita
    const{ mascota, propietario, fecha, hora, sintomas } = cita;

    //cuando el usuario presione el bot칩n de crear cita

    const submitCita = (e) => {
        e.preventDefault();
        console.log('Enviando...')
    }



    return (

        <Fragment>
            <h2> Crear cita </h2>

            <form
            onSubmit={submitCita}
            >
                <label> Nombre Mascota </label>

                <input
                    type="text"
                    name="mascota"
                    className="u-full-width"
                    placeholder="Nombre Mascota"
                    onChange={handleChange}
                    value={mascota}
                />

                <label> Nombre del due침o </label>

                <input
                    type="text"
                    name="propietario"
                    className="u-full-width"
                    placeholder="Nombre due침o de la mascota"
                    onChange={handleChange}
                    value={propietario}
                />

                <label> Fecha </label>

                <input
                    type="date"
                    name="fecha"
                    className="u-full-width"
                    onChange={handleChange}
                    value={fecha}
                />

                <label> Hora </label>

                <input
                    type="time"
                    name="hora"
                    className="u-full-width"
                    onChange={handleChange}
                    value={hora}
                />

                <label> Sintomas </label>

                <textarea
                    className="u-full-width"
                    name="sintomas"
                    onChange={handleChange}
                    value={sintomas}
                >

                </textarea>


                <button
                type="button"
                className="u-full-width button-primary"
                > 
                Agregar Cita
                </button>

            </form>
        </Fragment>

    );
};

export default Formulario;
